import { Component } from '@angular/core';
import { CommonModule } from '@angular/common'

@Component({
  selector: 'app-main-page',
  templateUrl: './main-page.component.html',
  styleUrls: ['./main-page.component.css'],
  standalone: true,
  imports: [
    CommonModule,
  ]
})
export class MainPageComponent {
  items = [
    { buttonText: 'Button 1', divText: 'Text 1' },
    { buttonText: 'Button 2', divText: 'Text 2' },
    { buttonText: 'Button 3', divText: 'Text 3' },
    // Adaugă mai multe elemente după necesități
  ];

  download() {
    // TODO
  }
}

<div *ngFor="let item of items" class="container">
  <button mat-button>{{ item.buttonText }}</button>
  <div class="text">{{ item.divText }}</div>
  <button mat-button (click)="download()">Download</button>
</div>
