import { Component } from '@angular/core';
import { FormBuilder, ReactiveFormsModule, Validators } from '@angular/forms';
import { MatInputModule } from '@angular/material/input';
import { MatButtonModule } from '@angular/material/button';
import { MatFormFieldModule } from '@angular/material/form-field';
import { Router } from '@angular/router';
import { CreateBaseDirService } from '../../core/services/create-base-dir.service';

@Component({
  selector: 'app-create-base-dir',
  templateUrl: './create-base-dir.component.html',
  styleUrls: ['./create-base-dir.component.css'],
  imports: [
    ReactiveFormsModule,
    MatInputModule,
    MatButtonModule,
    MatFormFieldModule,
  ],
  standalone: true
})
export class CreateBaseDirComponent {
  constructor(private readonly formBuilder: FormBuilder, private router: Router, private createBaseDirService: CreateBaseDirService) { }

  createBaseDirForm = this.formBuilder.group({
    DirectoryName: ['', Validators.required],
    IsPublic: ['']
  });

  create() {
    this.createBaseDirService.create(this.createBaseDirForm.value);
  }

}
<form class="formDir" [formGroup]="createBaseDirForm">
    <input class="formElems" matInput formControlName="DirectoryName" type="text" placeholder="Directory name" required>

    <div class="formElems">
      <input type="radio" id="public" formControlName="IsPublic" value="true" checked>
      <label for="public">Public</label>

      <input type="radio" id="private" formControlName="IsPublic" value="false">
      <label for="private">Private</label>
    </div>

  <button mat-raised-button color="primary" (click)="create()">Create</button>
</form>
