import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'dateFormatting',
  standalone: true
})
export class DateFormattingPipe implements PipeTransform {

  transform(value: Date, ...args: unknown[]): unknown {
    const localTimeString = value.toLocaleString(undefined, {
      year: 'numeric',
      month: 'numeric',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      second: 'numeric',
      timeZoneName: 'short',
    });

    return localTimeString;
  }

}
