import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatIconModule } from '@angular/material/icon';

@Component({
  selector: 'app-main-page',
  templateUrl: './main-page.component.html',
  styleUrls: ['./main-page.component.css'],
  standalone: true,
  imports: [
    CommonModule,
    MatIconModule
  ]
})
export class MainPageComponent {
  items = [
    { buttonText: 'dir1', divText: 'Text 1', icon: 'folder' },
    { buttonText: 'dir1284y5t324qhrieagc68esogr2cfiyq34fr6o8f2', divText: 'Text 2', icon: 'folder' },
    { buttonText: 'Fisier.pdf', divText: 'Text 3', icon: 'insert_drive_file' },
    // Adaugă mai multe elemente după necesități
  ];

  currentFolder: string = "Folder0";

  download() {
    // TODO
  }

}
<div class="navbar">
  <div>{{ currentFolder }}</div>
  <button mat-button>Previous folder</button>
  <button mat-button>Create Directory</button>
  <button mat-button>Upload</button>
</div>


<div *ngFor="let item of items" class="container">
  <button mat-button class="half-width">
    <mat-icon>{{ item.icon }}</mat-icon>
    <p>{{ item.buttonText }}</p>
  </button>
  <div class="text">{{ item.divText }}</div>
  <button mat-icon-button (click)="download()">
    <mat-icon>cloud_download</mat-icon>
  </button>
</div>
