import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatIconModule } from '@angular/material/icon';
import { Entry } from '../../data/interfaces/entry';
import { GetEntriesService } from '../../core/services/get-entries.service';
// import { Router } from '@angular/router';
import { Location } from '@angular/common';
import { DateFormattingPipe } from '../../core/pipes/date-formatting.pipe';

@Component({
  selector: 'app-main-page',
  templateUrl: './main-page.component.html',
  styleUrls: ['./main-page.component.css'],
  standalone: true,
  imports: [
    CommonModule,
    MatIconModule,
    DateFormattingPipe
  ]
})
export class MainPageComponent implements OnInit {
  /*items = [
    { buttonText: 'dir1', divText: 'Text 1', icon: 'folder' },
    { buttonText: 'dir1284y5t324qhrieagc68esogr2cfiyq34fr6o8f2', divText: 'Text 2', icon: 'folder' },
    { buttonText: 'Fisier.pdf', divText: 'Text 3', icon: 'insert_drive_file' },
    // Adaugă mai multe elemente după necesități
  ];*/
  items: Entry[] = [];
  pathToAsk: string = "";
  constructor(private location: Location, private readonly getEntriesService: GetEntriesService) { }

  ngOnInit(): void {
    this.pathToAsk = this.location.path();
    this.getEntriesService.getEntries(this.pathToAsk).subscribe(
      (data: any) => {
        this.items = data;
      },
      (error) => {
        console.error('Error during fetching entries: ' + error.error.message);
      }
    );
  }

  currentFolder: string = "Folder0";

  download() {
    // TODO
  }

}
<div class="navbar">
  <div>{{ currentFolder }}</div>
  <button mat-button>Previous folder</button>
  <button mat-button>Create Directory</button>
  <button mat-button>Upload</button>
</div>


<div *ngFor="let item of items" class="container">
  <button mat-button class="half-width">
    <mat-icon *ngIf="item.isDir">folder</mat-icon>
    <mat-icon *ngIf="!item.isDir">insert_drive_file</mat-icon>
    <p>{{ item.fileName }}</p>
  </button>
  <div class="text">{{ item.dateModified | dateFormatting }}</div>
  <button mat-icon-button (click)="download()">
    <mat-icon>cloud_download</mat-icon>
  </button>
</div>
